import React from 'react';
import NavBar from '../components/Navbar/NavBar';
import Footer from '../components/Footer';
// Import the actual images for the news articles
import newsIndiaToday from '../images/news-india-today.png'; // Placeholder for the India Today article image
import newsTwitter from '../images/news-twitter.png';       // Placeholder for the X/Twitter image
import newsJagran from '../images/news-jagran.jpg';         // Placeholder for the Dainik Jagran article image


const News = () => {
    const newsArticles = [
        {   
            title: 'Ahmedabad techie builds automated grenade detonation drone, Army orders 20 units',
            source: 'India Today',
            date: 'Jul 25, 2025',
            url: 'https://www.indiatoday.in/india/story/ahmedabad-engineer-builds-grenade-drone-gati-get-army-order-2760933-2025-07-25',
            image: newsIndiaToday,
        },
        {
            title: 'hmedabad-based techie Keshavkant Sharma builds automated grenade detonation drone, Army orders 20 units ',
            source: 'X (Formerly Twitter)',
            date: 'Jul 20, 2025',
            url: 'https://x.com/IndianTechGuide/status/1949054683483435145',
            image: newsTwitter,
        },
        {
            title: 'Indian Army to Induct AI-Powered \'Gati\' Grenade Dropping Drone',
            source: 'Dainik Jagran',
            date: 'Jul 26, 2025',
            url: 'https://jagran.com/gujarat/ahmedabad-gati-drone-indian-army-to-induct-ai-powered-grenade-dropping-drone-23997177.html',
            image: newsJagran,
        },
    ];

    return (
        <>
            <NavBar />
            <div className="bg-white py-24 lg:py-32">
                <div className="container mx-auto px-6">
                    <div className="text-center mb-16 animate-fadeInUp">
                        <h1 className="text-4xl font-extrabold text-gray-900">In The News</h1>
                        <p className="text-lg text-gray-600 mt-2">Read what others are saying about us.</p>
                    </div>
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                        {newsArticles.map((article, index) => (
                            <a
                                key={index}
                                href={article.url}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="block bg-gray-50 rounded-xl shadow-lg overflow-hidden transform hover:-translate-y-2 transition-transform duration-300 animate-fadeInUp group"
                                style={{ animationDelay: `${index * 0.1 + 0.2}s` }}
                            >
                                <img src={article.image} alt={article.title} className="w-full h-48 object-cover" />
                                <div className="p-6">
                                    <p className="text-sm text-gray-500 mb-2">{article.source} &bull; {article.date}</p>
                                    <h3 className="font-bold text-xl text-gray-800 group-hover:text-blue-600 transition-colors duration-300">{article.title}</h3>
                                </div>
                            </a>
                        ))}
                    </div>
                </div>
            </div>
            <Footer />
        </>
    );
};

export default News;
