import React from 'react';
import NavBar from '../components/Navbar/NavBar';
import Footer from '../components/Footer';
// Placeholder images - you can replace these with actual article thumbnails


const News = () => {
    const newsArticles = [
        {
            title: 'Protthapan Technologies Unveils New AI Drone',
            source: 'The Tech Chronicle',
            date: 'July 15, 2025',
            url: '#', // Replace with actual URL
        },
        {
            title: 'The Future of Surveillance: A Look at Protthapan\'s Latest Rover',
            source: 'Robotics Today',
            date: 'June 28, 2025',
            url: '#', // Replace with actual URL
        },
        {
            title: 'Indian Army Partners with Protthapan for Advanced Robotics',
            source: 'Defense News Weekly',
            date: 'May 10, 2025',
            url: '#', // Replace with actual URL
        },
    ];

    return (
        <>
            <NavBar />
            <div className="bg-white py-24 lg:py-32">
                <div className="container mx-auto px-6">
                    <div className="text-center mb-16 animate-fadeInUp">
                        <h1 className="text-4xl font-extrabold text-gray-900">In The News</h1>
                        <p className="text-lg text-gray-600 mt-2">Read what others are saying about us.</p>
                    </div>
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                        {newsArticles.map((article, index) => (
                            <a
                                key={index}
                                href={article.url}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="block bg-gray-50 rounded-xl shadow-lg overflow-hidden transform hover:-translate-y-2 transition-transform duration-300 animate-fadeInUp group"
                                style={{ animationDelay: `${index * 0.1 + 0.2}s` }}
                            >
                                <img src={article.image} alt={article.title} className="w-full h-48 object-cover" />
                                <div className="p-6">
                                    <p className="text-sm text-gray-500 mb-2">{article.source} &bull; {article.date}</p>
                                    <h3 className="font-bold text-xl text-gray-800 group-hover:text-blue-600 transition-colors duration-300">{article.title}</h3>
                                </div>
                            </a>
                        ))}
                    </div>
                </div>
            </div>
            <Footer />
        </>
    );
};

export default News;
