import React, { useState, useEffect } from 'react';
import { HashLink } from 'react-router-hash-link';
import img from '../../images/Black.-1-x-1.jpg';

const NavBar = () => {
    const [top, setTop] = useState(!window.scrollY);
    const [isOpen, setisOpen] = React.useState(false);

    function handleClick() {
        setisOpen(!isOpen);
    }

    useEffect(() => {
        const scrollHandler = () => {
            window.pageYOffset > 10 ? setTop(false) : setTop(true);
        };
        window.addEventListener('scroll', scrollHandler);
        return () => window.removeEventListener('scroll', scrollHandler);
    }, [top]);

    return (
        <nav className="bg-white shadow-md fixed top-0 w-full z-50 transition duration-300">
            <div className="container mx-auto flex justify-between items-center py-4 px-6">
                <div className="text-2xl font-bold text-gray-800">
                    <div className="flex flex-row justify-center items-center text-center font-extrabold">
                        <img alt="logo" className="w-12 h-12 rounded-full object-cover mr-4 shadow-lg" src={img} />
                        <HashLink smooth to="/#hero">
                            <h1 className="font-extrabold text-3xl md:text-4xl text-sky-500 tracking-tight leading-tight">Protthapan</h1>
                        </HashLink>
                    </div>
                </div>
                <div className="flex space-x-6">
                    <a href="#about" className="hover:text-blue-600 transition duration-300">About</a>
                    <a href="#services" className="hover:text-blue-600 transition duration-300">Services</a>
                    <a href="#gallery" className="hover:text-blue-600 transition duration-300">Gallery</a>
                    <a href="#contact" className="hover:text-blue-600 transition duration-300">Contact</a>
                </div>
            </div>
        </nav>
    );
};

export default NavBar;
