import React from 'react';
// You may need to install react-icons: npm install react-icons
import { FaCog, FaShieldAlt, FaWrench } from 'react-icons/fa';

const Services = () => {
    const services = [
        {
            icon: <FaCog className="w-12 h-12 text-blue-600 mb-4" />,
            title: 'Custom Drone Solutions',
            desc: 'Tailor-made UAVs designed to meet your specific operational requirements, from hardware to software.'
        },
        {
            icon: <FaShieldAlt className="w-12 h-12 text-blue-600 mb-4" />,
            title: 'Aerial Surveillance',
            desc: 'High-resolution imaging and real-time data streaming for security, monitoring, and tactical operations.'
        },
        {
            icon: <FaWrench className="w-12 h-12 text-blue-600 mb-4" />,
            title: 'Maintenance & Training',
            desc: 'Comprehensive support and training programs to ensure your team is proficient and your fleet is operational.'
        }
    ];

    return (
        <div className="py-16 bg-white text-gray-800" id="services">
            <div className="container mx-auto px-6">
                <div className="text-center mb-12 animate-fadeInUp">
                    <h2 className="text-4xl font-extrabold uppercase">Our Services</h2>
                    <div className='flex justify-center mt-4'>
                        <div className='w-24 border-b-4 border-blue-600'></div>
                    </div>
                </div>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
                    {services.map((service, index) => (
                        <div
                            key={index}
                            className="bg-gray-50 p-8 rounded-2xl shadow-lg text-center flex flex-col items-center transform hover:-translate-y-2 transition-transform duration-300 animate-fadeInUp"
                            style={{ animationDelay: `${index * 0.1}s` }}
                        >
                            {service.icon}
                            <h3 className="text-2xl font-bold text-gray-900 mb-3">{service.title}</h3>
                            <p className="text-md text-gray-600">{service.desc}</p>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Services;
